import { useLocation, useParams } from 'react-router-dom';
import { IMG_BASE_URL } from '../components/Movie';
import { dummy } from '../movieDummy';
const { results } = dummy;

console.log(results);

export default function MovieDetail() {
  const { itemId } = useParams();
  const { state } = useLocation();
  //클릭한 것만
  const item = getItemId(itemId);

  return (
    <div className="page-container">
      <div>
        <img src={IMG_BASE_URL + state.poster_path} style={{ width: '280px', height: '400px' }} alt="영화 포스터 이미지" />
      </div>
      <div lassName="movie-info">
        {/* <div style={{ fontSize: "18px", color: "white" }}>{id}</div> */}
        {
          (results = results.map(item => (
            <div key={item.id} style={{ fontSize: '18px', color: 'white' }}>
              {item.original_title}
            </div>
          )))
        }
      </div>
    </div>
  );
}
